(239, 1, '2021-06-03 15:10:14', '2021-06-03 19:10:14', '<!-- wp:group {\"align\":\"wide\"} -->\n<div class=\"wp-block-group alignwide\"><div class=\"wp-block-group__inner-container\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:block {\"ref\":197} /--></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":71,\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/logo_MILA-180x180-1.png\" alt=\"\" class=\"wp-image-71 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I am a researcher at <a href=\"https://mila.quebec/en/\">Mila</a> (Quebec AI Institute) interested in the learning aspects of AI, especially reinforcement learning and conscious processing.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":72,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/mcgill-bird/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/McGill-Bird-1024x1024.png\" alt=\"\" class=\"wp-image-72 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I am a Ph.D. student of Prof. <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and Prof. <a href=\"https://mila.quebec/yoshua-bengio/\">Yoshua Bengio</a>, at <a href=\"http://cs.mcgill.ca/\">School of Computer Science</a> of <a href=\"https://mcgill.ca/\">McGill University</a>. In summer 2020, I graduated as a MSc., under Prof. <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and Prof. <a href=\"https://www.cs.mcgill.ca/~chang/\">Xiao-Wen Chang</a>, with CGPA 4.0/4.0.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":73,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/consciousness/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/Consciousness.png\" alt=\"\" class=\"wp-image-73 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I work on aspects of conscious processing in reinforcement learning with Prof. <a href=\"https://mila.quebec/yoshua-bengio/\">Yoshua Bengio</a> and Prof. <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a>.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":74,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/dlut_logo/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/DLUT_logo.png\" alt=\"\" class=\"wp-image-74 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I graduated as an undergrad from <a href=\"https://en.dlut.edu.cn/\">Dalian University of Technology</a>, with major in Computer Science and Technology, and minor in Creative Practices (robotics). I received the award of the Outstanding Student Researcher of class 2018 and graduated with 90+ percentage (CGPA 4.0/4.0).</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":238,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/saxophone-brass-blow-jazz-instrument-512/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2021/06/saxophone-brass-blow-jazz-instrument-512.png\" alt=\"\" class=\"wp-image-238 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I had been studying saxophone performance, before switching to science &amp; engineering.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div></div>\n<!-- /wp:group -->', 'DISCOVERING INTELLIGENCE VIA COMPUTATION', '', 'inherit', 'closed', 'closed', '', '36-revision-v1', '', '', '2021-06-03 15:10:14', '2021-06-03 19:10:14', '', 36, 'http://localhost/homepage/?p=239', 0, 'revision', '', 0),
