(302, 1, '2021-09-25 22:46:01', '2021-09-26 02:46:01', '<!-- wp:block {\"ref\":197} /-->\n\n<!-- wp:paragraph -->\n<p><strong><em>Page Last Updated: 25 Sep. 2021</em></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {\"align\":\"wide\"} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:media-text {\"mediaId\":71,\"mediaType\":\"image\",\"mediaWidth\":15,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:15% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/logo_MILA-180x180-1.png\" alt=\"\" class=\"wp-image-71 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I am a researcher at&nbsp;<a href=\"https://mila.quebec/en/\">Mila</a>&nbsp;(Quebec AI Institute) interested in the learning aspects of AI, especially Reinforcement Learning (RL).</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":72,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/mcgill-bird/\",\"mediaType\":\"image\",\"mediaWidth\":15,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 15%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/McGill-Bird-1024x1024.png\" alt=\"\" class=\"wp-image-72 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I am a Ph.D. student of <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and <a href=\"https://mila.quebec/yoshua-bengio/\">Yoshua Bengio</a>, at <a href=\"http://cs.mcgill.ca/\">School of Computer Science</a> of <a href=\"https://mcgill.ca/\">McGill University</a>. In 2020, I graduated as a MSc., advised by <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and <a href=\"https://www.cs.mcgill.ca/~chang/\">Xiao-Wen Chang</a>, with CGPA 4.0/4.0.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaId\":244,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/logo-frq-couleur-1/\",\"mediaType\":\"image\",\"mediaWidth\":20,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:20% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2021/06/logo-frq-couleur-1.png\" alt=\"\" class=\"wp-image-244 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I am a recipient of the <a href=\"https://frq.gouv.qc.ca/en/\" data-type=\"URL\" data-id=\"https://frq.gouv.qc.ca/en/\">FRQ (Nature and Technologies)</a> funds, ranked<strong> <span class=\"has-inline-color has-accent-color\">1st </span></strong>place among all 2020 contenders. </p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":74,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/dlut_logo/\",\"mediaType\":\"image\",\"mediaWidth\":15,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 15%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/DLUT_logo.png\" alt=\"\" class=\"wp-image-74 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I was an undergrad at <a href=\"https://en.dlut.edu.cn/\">Dalian University of Technology</a>, with major in Computer Science and Technology, and minor in Creative Practices (robotics). I received the Outstanding Student Researcher award of class 2018 and graduated with 90+ percentage (CGPA 4.0/4.0).</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaId\":238,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/saxophone-brass-blow-jazz-instrument-512/\",\"mediaType\":\"image\",\"mediaWidth\":15,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide is-stacked-on-mobile\" style=\"grid-template-columns:15% auto\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2021/06/saxophone-brass-blow-jazz-instrument-512.png\" alt=\"\" class=\"wp-image-238 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">I had been studying saxophone performance, before switching to science &amp; engineering.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->', 'DISCOVERING INTELLIGENCE VIA COMPUTATION', '', 'inherit', 'closed', 'closed', '', '36-revision-v1', '', '', '2021-09-25 22:46:01', '2021-09-26 02:46:01', '', 36, 'http://localhost/homepage/?p=302', 0, 'revision', '', 0),
