(263, 1, '2021-07-29 13:12:14', '2021-07-29 17:12:14', '<!-- wp:group {\"align\":\"wide\"} -->\n<div class=\"wp-block-group alignwide\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:block {\"ref\":197} /--></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":71,\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/logo_MILA-180x180-1.png\" alt=\"\" class=\"wp-image-71 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I am a researcher at&nbsp;<a href=\"https://mila.quebec/en/\">Mila</a>&nbsp;(Quebec AI Institute) interested in the learning aspects of AI, especially reinforcement learning and conscious processing.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":72,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/mcgill-bird/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/McGill-Bird-1024x1024.png\" alt=\"\" class=\"wp-image-72 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I am a Ph.D. student of <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and <a href=\"https://mila.quebec/yoshua-bengio/\">Yoshua Bengio</a>, at <a href=\"http://cs.mcgill.ca/\">School of Computer Science</a> of <a href=\"https://mcgill.ca/\">McGill University</a>. In summer 2020, I graduated as a MSc., under the supervision of <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and <a href=\"https://www.cs.mcgill.ca/~chang/\">Xiao-Wen Chang</a>, with CGPA 4.0/4.0.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":244,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/logo-frq-couleur-1/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2021/06/logo-frq-couleur-1.png\" alt=\"\" class=\"wp-image-244 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I am a recipient of the <a href=\"https://frq.gouv.qc.ca/en/\" data-type=\"URL\" data-id=\"https://frq.gouv.qc.ca/en/\">FRQ (Nature and Technologies)</a> funds, ranked<strong> <span class=\"has-inline-color has-accent-color\">1st </span></strong>among all contenders. </p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":74,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/dlut_logo/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2020/06/DLUT_logo.png\" alt=\"\" class=\"wp-image-74 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I finished my undergrad at <a href=\"https://en.dlut.edu.cn/\">Dalian University of Technology</a>, with major in Computer Science and Technology, and minor in Creative Practices (robotics). I received the award of the Outstanding Student Researcher of class 2018 and graduated with 90+ percentage (CGPA 4.0/4.0).</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text -->\n\n<!-- wp:media-text {\"mediaPosition\":\"right\",\"mediaId\":238,\"mediaLink\":\"http://localhost/homepage/the-new-umoma-opens-its-doors-2/saxophone-brass-blow-jazz-instrument-512/\",\"mediaType\":\"image\",\"mediaWidth\":23,\"mediaSizeSlug\":\"full\"} -->\n<div class=\"wp-block-media-text alignwide has-media-on-the-right is-stacked-on-mobile\" style=\"grid-template-columns:auto 23%\"><figure class=\"wp-block-media-text__media\"><img src=\"http://localhost/homepage/wp-content/uploads/2021/06/saxophone-brass-blow-jazz-instrument-512.png\" alt=\"\" class=\"wp-image-238 size-full\"/></figure><div class=\"wp-block-media-text__content\"><!-- wp:paragraph {\"dropCap\":true,\"placeholder\":\"Content…\",\"fontSize\":\"small\"} -->\n<p class=\"has-drop-cap has-small-font-size\">I had been studying saxophone performance, before switching to science &amp; engineering.</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:media-text --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->', 'DISCOVERING INTELLIGENCE VIA COMPUTATION', '', 'inherit', 'closed', 'closed', '', '36-revision-v1', '', '', '2021-07-29 13:12:14', '2021-07-29 17:12:14', '', 36, 'http://localhost/homepage/?p=263', 0, 'revision', '', 0),
(156, 1, '2020-09-13 05:07:08', '2020-09-13 05:07:08', '<!-- wp:group {\"align\":\"wide\"} -->\n<div class=\"wp-block-group alignwide\"><div class=\"wp-block-group__inner-container\"><!-- wp:heading {\"align\":\"center\",\"level\":6,\"textColor\":\"accent\"} -->\n<h6 class=\"has-text-align-center has-accent-color has-text-color\">DISCOVERING INTELLIGENCE VIA COMPUTATION</h6>\n<!-- /wp:heading --></div></div>\n<!-- /wp:group -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":66.66} -->\n<div class=\"wp-block-column\" style=\"flex-basis:66.66%\"><!-- wp:paragraph {\"dropCap\":true} -->\n<p class=\"has-drop-cap\"><img class=\"wp-image-71\" style=\"width: 25px;\" src=\"http://localhost/homepage/wp-content/uploads/2020/06/logo_MILA-180x180-1.png\" alt=\"\"> I am a researcher at <a href=\"https://mila.quebec/en/\">Mila</a> (Quebec AI Institute) interested in the general aspects of AI, especially Reinforcement Learning and Meta-Learning.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"dropCap\":true} -->\n<p class=\"has-drop-cap\"><img class=\"wp-image-72\" style=\"width: 25px;\" src=\"http://localhost/homepage/wp-content/uploads/2020/06/McGill-Bird.png\" alt=\"\"> I am a Ph.D. student of Prof. <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and Prof. <a href=\"https://mila.quebec/yoshua-bengio/\">Yoshua Bengio</a>, at <a href=\"http://cs.mcgill.ca/\">School of Computer Science</a> of <a href=\"https://mcgill.ca/\">McGill University</a>. In summer 2020, I graduated as a MSc., under Prof. <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a> and Prof. <a href=\"https://www.cs.mcgill.ca/~chang/\">Xiao-Wen Chang</a>, with CGPA 4.0/4.0.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"dropCap\":true} -->\n<p class=\"has-drop-cap\"><img class=\"wp-image-73\" style=\"width: 25px;\" src=\"http://localhost/homepage/wp-content/uploads/2020/06/Consciousness.png\" alt=\"\"> I am currently working on conscious planning with Prof. <a href=\"https://mila.quebec/yoshua-bengio/\">Yoshua Bengio</a> and Prof. <a href=\"https://www.cs.mcgill.ca/~dprecup/\">Doina Precup</a>.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"dropCap\":true} -->\n<p class=\"has-drop-cap\"><img class=\"wp-image-74\" style=\"width: 25px;\" src=\"http://localhost/homepage/wp-content/uploads/2020/06/DLUT_logo.png\" alt=\"\"> I studied as an undergrad at&nbsp;<a href=\"https://en.dlut.edu.cn/\">Dalian University of Technology</a>, with major in Computer Science and Technology, and minor in Creative Practices (humanoid robotics), 2014 to 2018. I received the award of Distinguished Student Researcher (the only one for class 2018) and graduated with CGPA 4.0/4.0.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":33.33} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33.33%\"><!-- wp:core-embed/instagram {\"url\":\"https://www.instagram.com/p/CDVH5T-FBiB/\",\"type\":\"rich\",\"providerNameSlug\":\"instagram\",\"className\":\"\"} -->\n<figure class=\"wp-block-embed-instagram wp-block-embed is-type-rich is-provider-instagram\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.instagram.com/p/CDVH5T-FBiB/\n</div></figure>\n<!-- /wp:core-embed/instagram --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Bonjour! Hello! 你好!', '', 'inherit', 'closed', 'closed', '', '36-revision-v1', '', '', '2020-09-13 05:07:08', '2020-09-13 05:07:08', '', 36, 'http://localhost/homepage/2020/09/13/36-revision-v1/', 0, 'revision', '', 0),
